 function getsorttype($talbe,$img=0){ if($_REQUEST['order']==$talbe){ if($_REQUEST['sort']=="desc"){ $sorttype="asc"; }else{ $sorttype="desc"; } if($img==1){ $sorttype= '<img border="0" src="'.APP_PUBLIC_URL.'/images/'.$sorttype.'.gif">'; } }else{ $sorttype="asc"; if($img==1){ $sorttype= ''; } } return $sorttype; } function id_To_EValue($tb,$col,$o_col,$id) { if(trim($id)=="") return "参数错误"; else { $thisdao=D($tb); $thisdao->Cache(true); $list=$thisdao->find($o_col.'='.$id,$col); return $list[$col]; } } function getcolor($id) { if(S('color'.$id)==""){ $thisdao=D('sub_color'); $list=$thisdao->find('id='.$id,'title'); S('color'.$id,$list['title']); return $list['title']; } else { return S('color'.$id); } } function getfielddetail($datetype) { $field=D('sub_fieldtype'); $fielddate=$field->getbyid($datetype); if($fielddate['primary']==1){ $date['img']='FieldKey_small.png'; $date['iskey']=1; }else{ switch ($fielddate['type']){ case 'VARCHAR': $date['img']='Field_small_char.png'; $date['iskey']=0; break; case 'TEXT': $date['img']='Field_small_char.png'; $date['iskey']=0; break; case 'CHAR': $date['img']='Field_small_char.png'; $date['iskey']=0; break; case 'INT': $date['img']='Field_small_int.png'; $date['iskey']=0; break; case 'SMALLINT': $date['img']='Field_small_int.png'; $date['iskey']=0; break; default: $date['img']='Field_small_char.png'; $date['iskey']=0; break; } } $date['type']=$fielddate['type']; $date['leng']=$fielddate['leng']; return $date; } function makeselect($name,$table,$idt='',$where='',$option='title',$f_idvalue='id'){ if($option==''){$option='title';} if($where==''){$where='';} if($idt==''){$idt='';} if($f_idvalue==''){$f_idvalue='id';} $tmp= "	<select name=".$name.">"; $thisdao=D($table); $thisdao->Cache(true); $list=$thisdao->findAll($where); foreach ($list as $row) { if ($idt==$row[$f_idvalue] ){$var="selected";} $thiscolor=getcolor($row['id']); $tmp.="<option value='".$row[$f_idvalue]."'  ".$var."  style='background:".$thiscolor.";' title='参数:".$row[$f_idvalue]."标题:".$row[$option]."'>".$row[$option]."</option>"; $var=''; $thiscolor=''; } $tmp.= "</select>"; echo $tmp; } function makeoption($table,$id='id',$where='',$option='title',$order,$outadd){ if($option==''){$option='title';} if($where==''){$where='';} if($id==''){$id='';} if($order==''){$order='id desc';} if($outadd==''){$outadd='>';} $thisdao=D($table); $options=explode(',',$option); $list=$thisdao->findAll($where,$option.','.$id,$order); for ($i = 0; $i < count($list); $i++) { $optionkey[]=$list[$i][$id]; foreach($options as $optionlist){ $var.=$outadd.$list[$i][$optionlist]; } $optionval[]=$var; $var=''; } $tmp=array_combine($optionkey,$optionval); return $tmp; } function copytable($daoname,$inpid,$pidf='pid',$inoldid){ $daoname; $inpid; $pidf; $inoldid; $table=D($daoname); foreach ($tabledata as $creatdb){ $creatdb[$pidf]=$inpid; $oldid=$creatdb['id']; $table->create($creatdb); $table->add(); $newid=$table->getLastInsID(); copytable('sys_fields',$newid,'pid',$oldid); } } function delbypid($daoname,$inpid,$pidf='pid'){ $table=D($daoname); $tabledata=$table->findall($pidf.'='.$inpid); foreach ($tabledata as $creatdb){ $oldid=$creatdb['id']; delbypid('sys_fields',$oldid,'pid'); } $table->deleteAll('pid='.$inpid); } function clean_html($value){ $value = htmlspecialchars($value, ENT_QUOTES); $value = strtr($value, array( '(' => '&#40;', ')' => '&#41;' )); return nl2br($value); } function un_clean_html($value){ $value = htmlspecialchars_decode($value); $value = strtr($value, array( '&#40;' => '(', '&#41;' => ')' )); return nl2br($value); } function toDate($time,$format='Y年m月d日 H:i:s') { if( empty($time)) { return ''; } $format = str_replace('#',':',$format); return date(auto_charset($format),$time); } ?><?php
